import Vue from 'vue'
import App from './App.vue'
import router from './router'

import'mint-ui/lib/style.css'

Vue.config.productionTip = false
//5: 设置请求的根路径 
//Vue.http.options.root = "http://127.0.0.1/vue_ser/";
//6:全局设置post 时候表音的数据组织格式为 application/x-www-form-urlencoded
//Vue.http.options.emulateJSON = true;

// 导入 MUI 的样式表， 和 Bootstrap 用法没有差别
import './lib/mui/css/mui.css'
// 导入 MUI 的样式表，扩展图标样式，购物车图标
// 还需要加载图标字体文件
import './lib/mui/css/icons-extra.css'

//引入组件mintui库Header
import {Header,Swipe,SwipeItem,Button} from "mint-ui";//引入


Vue.component(Header.name,Header);//注册
Vue.component(Swipe.name,Swipe);
Vue.component(SwipeItem.name,SwipeItem);
//Header.name就是标签ht-header

Vue.component(Button.name,Button)
console.log(Header.name);

//引入VueResource发送ajax请求
import VueResource from "vue-resource";//引入库中所有组件
Vue.use(VueResource);//库中所有组件注册

Vue.filter("dateFilter",function (val) {
    var date=new Date(val);
    var y=date.getFullYear();
    var m=date.getMonth()+1;
    var d=date.getDate();
    m<10&&(m="0"+m);
    d<10&&(d="0"+d);
    return `${y}-${m}-${d}`
  }
)
Vue.filter("datetimeFilter",function(val){
  var date=new Date(val);
  var y=date.getFullYear();
  var m=date.getMonth()+1;
  var d=date.getDate();
  var h=date.getHours();
  var mi=date.getMinutes();
  var s=date.getSeconds();
  m<10&&(m="0"+m);
  d<10&&(d="0"+d);
  return `${y}-${m}-${d} ${h}:${mi}:${s}`
})

new Vue({
  router,
  render: h => h(App)
}).$mount('#app')
